import React from 'react';
import Style from './About.module.scss';
import Terminal from "./Terminal";
import {Box} from "@mui/material";
import {info} from "../../info/Info";


export default function About({innerRef}) {
    const firstName = info.firstName.toLowerCase()

    function aboutMeText() {
        return <>
            <p><span style={{color: info.baseColor}}></span>
                {info.bio}
            </p>
        </>;
    }

    function skillsText() {
        return <>
            <p><span style={{color: info.baseColor}}>skills/tools </span> ls</p>
           
            <p style={{color: info.baseColor}}> Proficient With</p>
            
            <ul className={Style.skills}>
           
                {info.skills.proficientWith.map((proficiency, index) => <li key={index}>{proficiency}</li>)}
           
            </ul>
           
            <p style={{color: info.baseColor}}> Exposed To</p>
           
            <ul className={Style.skills}>
           
                {info.skills.exposedTo.map((skill, index) => <li key={index}>{skill}</li>)}
           
            </ul>
        </>;
    }

    function miscText() {
        return <>
                        <p><span style={{color: info.baseColor}}>hobbies/interests </span> ls</p>
            <ul>
                {info.hobbies.map((hobby, index) => (
                    <li key={index}><Box component={'span'} mr={'1rem'}>{hobby.emoji}</Box>{hobby.label}</li>
                ))}
            </ul>
        </>;
    }

    return (
        <Box ref={innerRef} display={'flex'} flexDirection={'row'} alignItems={'center'} mt={'2rem'} id={'about'}>
            <Terminal text={aboutMeText()}/>
            <Terminal text={skillsText()}/>
            <Terminal text={miscText()}/>
        </Box>
    )
}
